// src/components/VideoRoom.jsx
import React, { useEffect, useRef, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import { Mic, MicOff, Video, VideoOff, LogOut, Send } from 'lucide-react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './VidroRoom.css';

const socket = io();

export default function VideoRoom() {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const localVideoRef = useRef();
  const peersRef = useRef({});
  const localStreamRef = useRef();

  const [mediaStarted, setMediaStarted] = useState(false);
  const [remoteStreams, setRemoteStreams] = useState([]);
  const [messages, setMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);

  // Retrieve user's email from localStorage (set at login)
  const userEmail = localStorage.getItem('email') || 'Anonymous';

  useEffect(() => {
    return () => {
      Object.values(peersRef.current).forEach(pc => pc.close());
      socket.disconnect();
    };
  }, []);

  const startMedia = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: { ideal: 'user' } },
        audio: true
      });
      localStreamRef.current = stream;

      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }
      setTimeout(() => {
        if (localVideoRef.current && !localVideoRef.current.srcObject) {
          localVideoRef.current.srcObject = stream;
        }
      }, 500);

      // Join signaling room
      socket.emit('join-room', roomId, socket.id, userEmail);

      // Handle new user connections
      socket.on('user-connected', async userId => {
        if (userId === socket.id) return;
        const pc = new RTCPeerConnection({ iceServers: [{ urls: 'stun:stun.l.google.com:19302' }] });
        peersRef.current[userId] = pc;
        stream.getTracks().forEach(track => pc.addTrack(track, stream));

        pc.onicecandidate = e => {
          if (e.candidate) {
            socket.emit('ice-candidate', { target: userId, candidate: e.candidate });
          }
        };

        pc.ontrack = e => addRemoteStream(userId, e.streams[0]);

        const offer = await pc.createOffer();
        await pc.setLocalDescription(offer);
        socket.emit('offer', { target: userId, sdp: offer, email: userEmail });
      });

      // Receive offer
      socket.on('offer', async ({ sender, sdp, email }) => {
        const pc = new RTCPeerConnection({ iceServers: [{ urls: 'stun:stun.l.google.com:19302' }] });
        peersRef.current[sender] = pc;
        stream.getTracks().forEach(track => pc.addTrack(track, stream));

        pc.onicecandidate = e => {
          if (e.candidate) {
            socket.emit('ice-candidate', { target: sender, candidate: e.candidate });
          }
        };

        pc.ontrack = e => addRemoteStream(sender, e.streams[0]);

        await pc.setRemoteDescription(new RTCSessionDescription(sdp));
        const answer = await pc.createAnswer();
        await pc.setLocalDescription(answer);
        socket.emit('answer', { target: sender, sdp: answer, email: userEmail });
      });

      // Receive answer
      socket.on('answer', async ({ sender, sdp }) => {
        const pc = peersRef.current[sender];
        if (pc) await pc.setRemoteDescription(new RTCSessionDescription(sdp));
      });

      // ICE candidates
      socket.on('ice-candidate', ({ sender, candidate }) => {
        const pc = peersRef.current[sender];
        if (pc) pc.addIceCandidate(new RTCIceCandidate(candidate));
      });

      // User disconnected
      socket.on('user-disconnected', id => {
        if (peersRef.current[id]) {
          peersRef.current[id].close();
          delete peersRef.current[id];
          removeRemoteStream(id);
        }
      });

      // Chat messages
      socket.on('chat-message', data => {
        setMessages(prev => [...prev, { user: data.email, message: data.message }]);
      });

      setMediaStarted(true);
    } catch (err) {
      console.error('Error accessing media devices.', err);
      alert('Unable to access camera and microphone. Please allow permissions.');
    }
  };

  const addRemoteStream = (id, stream) => {
    setRemoteStreams(prev => prev.some(p => p.id === id) ? prev : [...prev, { id, stream }]);
  };

  const removeRemoteStream = id => setRemoteStreams(prev => prev.filter(p => p.id !== id));

  const toggleMic = () => {
    const tracks = localStreamRef.current?.getAudioTracks() || [];
    tracks.forEach(track => track.enabled = !track.enabled);
    setMicOn(p => !p);
  };

  const toggleCam = () => {
    const tracks = localStreamRef.current?.getVideoTracks() || [];
    tracks.forEach(track => track.enabled = !track.enabled);
    setCamOn(p => !p);
  };

  const sendMessage = () => {
    if (!chatInput.trim()) return;
    const msg = { email: userEmail, message: chatInput.trim() };
    socket.emit('chat-message', msg);
    setMessages(prev => [...prev, msg]);
    setChatInput('');
  };

  const leaveRoom = () => {
    navigate('/dashboard'); window.location.reload();
  };

  if (!mediaStarted) {
    return (
      <div className="d-flex justify-content-center align-items-center vh-100">
        <button className="btn btn-primary" onClick={startMedia}>
          Start Camera & Join Room
        </button>
      </div>
    );
  }

  return (
    <div className="d-flex flex-column flex-lg-row min-vh-100">
      <div className="flex-fill video-bg p-3 position-relative">
        <div className="d-flex flex-wrap justify-content-center align-items-start gap-3">
          {remoteStreams.length ? remoteStreams.map(peer => (
            <video
              key={peer.id}
              ref={v => v && (v.srcObject = peer.stream)}
              autoPlay muted playsInline
              className="vid-lg rounded"
            />
          )) : <div className="waiting-text">Waiting for others...</div>}
        </div>
        <video
          ref={localVideoRef}
          autoPlay muted playsInline
          className="vid-sm rounded border"
          style={{ position: 'absolute', bottom: 16, right: 16, width: 150, height: 100 }}
        />
        <div className="controls d-flex justify-content-center gap-2 p-2">
          <button className="btn btn-outline-light" onClick={toggleMic}>
            {micOn ? <Mic /> : <MicOff />}
          </button>
          <button className="btn btn-outline-light" onClick={toggleCam}>
            {camOn ? <Video /> : <VideoOff />}
          </button>
          <button className="btn btn-danger" onClick={leaveRoom}>
            <LogOut />
          </button>
        </div>
      </div>

      <div className="chat-section bg-light d-flex flex-column p-3" style={{ width: '100%', maxWidth: 400 }}>
        <div className="flex-grow-1 overflow-auto mb-3">
          {messages.map((m, i) => (
            <div key={i} className="mb-2">
              <strong>{m.user === userEmail ? 'You' : m.user}:</strong> {m.message}
            </div>
          ))}
        </div>
        <div className="d-flex">
          <input
            className="form-control me-2"
            placeholder="Type a message..."
            value={chatInput}
            onChange={e => setChatInput(e.target.value)}
            onKeyDown={e => e.key === 'Enter' && sendMessage()}
          />
          <button className="btn btn-primary" onClick={sendMessage}><Send /></button>
        </div>
      </div>
    </div>
  );
}
