import React, { useEffect, useRef, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';
import { Mic, MicOff, Video, VideoOff, LogOut, Send } from 'lucide-react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './VidroRoom.css';

const socket = io('https://your-server-domain-or-ip:PORT'); // Replace with your deployed signaling server

export default function VideoRoom() {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const localVideoRef = useRef();
  const peersRef = useRef({});
  const [remoteStreams, setRemoteStreams] = useState([]);
  const [messages, setMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);
  const localStreamRef = useRef();

  useEffect(() => {
    const pcConfig = {
      iceServers: [
        { urls: 'stun:stun.l.google.com:19302' },
        {
          urls: 'turn:openrelay.metered.ca:80',
          username: 'openrelayproject',
          credential: 'openrelayproject'
        }
      ]
    };

    navigator.mediaDevices.getUserMedia({ video: true, audio: true })
      .then(stream => {
        localStreamRef.current = stream;
        localVideoRef.current.srcObject = stream;

        socket.emit('join-room', { roomId, userId: socket.id });

        socket.on('user-connected', async ({ userId }) => {
          if (userId === socket.id) return;
          const pc = createPeer(userId, stream, pcConfig);
          peersRef.current[userId] = pc;

          const offer = await pc.createOffer();
          await pc.setLocalDescription(offer);

          socket.emit('offer', {
            target: userId,
            sdp: offer,
            sender: socket.id
          });
        });

        socket.on('offer', async ({ sender, sdp }) => {
          const pc = createPeer(sender, stream, pcConfig);
          peersRef.current[sender] = pc;

          await pc.setRemoteDescription(new RTCSessionDescription(sdp));
          const answer = await pc.createAnswer();
          await pc.setLocalDescription(answer);

          socket.emit('answer', {
            target: sender,
            sdp: answer,
            sender: socket.id
          });
        });

        socket.on('answer', async ({ sender, sdp }) => {
          const pc = peersRef.current[sender];
          if (pc) await pc.setRemoteDescription(new RTCSessionDescription(sdp));
        });

        socket.on('ice-candidate', ({ sender, candidate }) => {
          const pc = peersRef.current[sender];
          if (pc) pc.addIceCandidate(new RTCIceCandidate(candidate));
        });

        socket.on('user-disconnected', id => {
          if (peersRef.current[id]) {
            peersRef.current[id].close();
            delete peersRef.current[id];
            setRemoteStreams(prev => prev.filter(v => v.id !== id));
          }
        });

        socket.on('chat-message', data => {
          setMessages(prev => [...prev, data]);
        });

      }).catch(err => console.error('getUserMedia error:', err));

    return () => {
      Object.values(peersRef.current).forEach(pc => pc.close());
      socket.disconnect();
    };
  }, [roomId]);

  const createPeer = (userId, stream, config) => {
    const pc = new RTCPeerConnection(config);

    stream.getTracks().forEach(track => pc.addTrack(track, stream));

    pc.onicecandidate = event => {
      if (event.candidate) {
        socket.emit('ice-candidate', {
          target: userId,
          candidate: event.candidate,
          sender: socket.id
        });
      }
    };

    pc.ontrack = event => {
      const incomingStream = event.streams[0];
      setRemoteStreams(prev => {
        const exists = prev.find(p => p.id === userId);
        if (!exists) return [...prev, { id: userId, stream: incomingStream }];
        return prev;
      });
    };

    return pc;
  };

  const toggleMic = () => {
    const audioTracks = localStreamRef.current?.getAudioTracks();
    audioTracks?.forEach(track => (track.enabled = !track.enabled));
    setMicOn(p => !p);
  };

  const toggleCam = () => {
    const videoTracks = localStreamRef.current?.getVideoTracks();
    videoTracks?.forEach(track => (track.enabled = !track.enabled));
    setCamOn(p => !p);
  };

  const sendMessage = () => {
    if (!chatInput.trim()) return;
    const message = { user: socket.id, message: chatInput };
    socket.emit('chat-message', message);
    setMessages(prev => [...prev, message]);
    setChatInput('');
  };

  const leaveRoom = () => {
    navigate('/dashboard');
    window.location.reload();
  };

  return (
    <div className="d-flex flex-column flex-lg-row min-vh-100">
      <div className="flex-fill video-bg p-3 position-relative">
        <div className="d-flex flex-wrap justify-content-center align-items-start gap-3">
          {remoteStreams.length ? remoteStreams.map(peer => (
            <video
              key={peer.id}
              ref={video => video && (video.srcObject = peer.stream)}
              autoPlay
              playsInline
              className="vid-lg rounded"
            />
          )) : <div className="waiting-text">Waiting for others...</div>}
        </div>
        <div className="local-video-wrapper">
          <video
            ref={localVideoRef}
            autoPlay
            muted
            playsInline
            className="vid-sm rounded border"
          />
        </div>
        <div className="controls d-flex justify-content-center gap-2 p-2">
          <button className="btn btn-outline-light btn-icon" onClick={toggleMic}>
            {micOn ? <Mic /> : <MicOff />}
          </button>
          <button className="btn btn-outline-light btn-icon" onClick={toggleCam}>
            {camOn ? <Video /> : <VideoOff />}
          </button>
          <button className="btn btn-outline-danger btn-icon" onClick={leaveRoom}>
            <LogOut />
          </button>
        </div>
      </div>

      <div className="chat-section bg-light d-flex flex-column p-3">
        <div className="flex-grow-1 overflow-auto mb-3">
          {messages.map((msg, i) => (
            <div key={i} className={`chat-msg ${msg.user === socket.id ? 'self' : 'other'}`}>
              <strong>{msg.user === socket.id ? 'You' : msg.user.slice(0, 6)}:</strong> {msg.message}
            </div>
          ))}
        </div>
        <div className="d-flex">
          <input
            className="form-control me-2"
            placeholder="Type a message..."
            value={chatInput}
            onChange={e => setChatInput(e.target.value)}
            onKeyDown={e => e.key === 'Enter' && sendMessage()}
          />
          <button className="btn btn-primary" onClick={sendMessage}><Send /></button>
        </div>
      </div>
    </div>
  );
}
