import React from "react";
import { useState } from "react";
import './login.css'

const LoginSignup = () => {
    const [isLogin, setIsLogin] = useState(true);
    const [form, setForm] = useState({ username: '', password: '' });
    const navigate = useNavigate();
  
    const handleSubmit = async e => {
      e.preventDefault();
      const endpoint = isLogin ? '/api/auth/login' : '/api/auth/register';
      try {
        const res = await axios.post(endpoint, form);
        if (isLogin) {
          localStorage.setItem('token', res.data.token);
          navigate('/dashboard');
        } else {
          alert('Registered successfully, please login.');
          setIsLogin(true);
        }
      } catch (err) {
        alert(err.response?.data?.error || 'Something went wrong');
      }
    };
  
    const switchMode = () => {
      setForm({ username: '', password: '' });
      setIsLogin(!isLogin);
    };
    
      const toggleAuthMode = () => {
        setIsLogin(!isLogin);
      };
    
      const openModal = () => {
        setShowModal(true);
      };
    
      const closeModal = () => {
        window.location.reload();
        window.location.href = "/";
      };
  return (
    <>
  <div className="modal d-block imaged" tabIndex="-1" style={{ backgroundColor: "rgba(0, 0, 0, 0.5)" }}>
    <div className="modal-dialog modal-dialog-centered">
      <div className="modal-content">
        <div className="modal-header">
          <h5 className="modal-title">{isLogin ? 'Login' : 'Signup'}</h5>
          <button type="button" className="btn-close" onClick={closeModal}></button>
        </div>
        <div className="modal-body">
          <form onSubmit={handleSubmit}>
            {!isLogin && (
              <div className="mb-3">
                <label className="form-label">Username</label>
                <input type="text" className="form-control" placeholder="Enter username" required />
              </div>
            )}
            <div className="mb-3">
              <label className="form-label">Email address</label>
              <input type="email" className="form-control" placeholder="Enter email" required />
            </div>
            <div className="mb-3">
              <label className="form-label">Password</label>
              <input type="password" className="form-control" placeholder="Enter password" required />
            </div>

            <button type="submit" className="btn btn-primary w-100 mb-3"  onClick={switchMode}>
              {isLogin ? 'Login' : 'Signup'}
            </button>
          </form>
          {isLogin ? (
            <div className="text-center">
              <a href="#" className="d-block mb-2">Forgot Password?</a>
              <button type="button" className="btn btn-link" onClick={toggleAuthMode}>
                New User? Signup
              </button>
            </div>
          ) : (
            <div className="text-center">
              <button type="button" className="btn btn-link" onClick={toggleAuthMode}>
                Already have an account? Login
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
    </div>
        </>
      );
    };
    

export default LoginSignup;
